LogOutMethod = 0


def mylog(*BUF):
    #  输出到文件
    if LogOutMethod == 1:
        with open(log_file, 'a') as log:
            print(dt.datetime.now().strftime('%F %T:%f'), file=log, end=' ')
            for i in BUF:
                print(i, file=log, end=' ')
            print('', file=log)

    # 输出到控制台
    elif LogOutMethod == 2:
        print(dt.datetime.now().strftime('%F %T:%f'), end=' ')
        for i in BUF:
            print(i, end=' ')
        print(end='\n')


