#  @ 功能：定期刷新显示左上角状态标签
class ViewSta(Frame):
    msec = 100  # 标签更新频率

    def __init__(self, parent=None, **kw):
        Frame.__init__(self, parent, kw)
        mutex.acquire()
        self._running = False
        mutex.release()
        self.str1 = StringVar()
        Lab = Label(self, textvariable=self.str1,  # 设置文本内容
                    width=0,  # 设置label的宽度
                    height=0,  # 设置label的高度
                    justify='left',  # 设置文本对齐方式：左对齐
                    anchor='nw',  # 设置文本在label的方位：西北方位
                    font=('宋体', 8),  # 设置字体，字号
                    fg='red',  # 设置前景色
                    bg='white',  # 设置背景色
                    padx=0,  # 设置x方向内边距
                    pady=0)  # 设置y方向内边距
        Lab.pack()
        self.flag = True

    def _update(self):
        self._setstr()
        self.timer = self.after(self.msec, self._update)

    def _setstr(self):
        self.str1.set(StatusText)

    def start(self):
        self._update()
        self.pack(side=TOP)


